/* Some misc helpers */
.relative {
  position: relative; }

.justify-end {
  justify-content: flex-end; }

.text-left {
  text-align: left; }

.text-right {
  text-align: right; }

.pointer {
  cursor: pointer; }

.scrollable {
  overflow: auto;
  max-height: 100%; }

.va-middle {
  vertical-align: middle; }

a {
  text-decoration: none; }

/* --------------- */
/*  LAYOUT         */
/* --------------- */
html {
  background: #2d3b44; }

* {
  font: normal 300 1em "Roboto";
  color: white; }

.danger, .topics-view .deleteTopicsModal h2 {
  color: #ff756f; }

.document-wrapper {
  display: flex;
  flex-direction: column;
  height: 100%; }

.page-wrapper {
  display: flex;
  flex: 1;
  max-height: calc(100vh - 112px); }

.content-wrapper {
  display: flex;
  flex-direction: column;
  flex: 1;
  padding: 32px; }

.grid-wrapper {
  display: flex;
  flex-direction: column;
  flex: 1;
  max-height: 100%; }

.grid {
  display: flex;
  flex: 1;
  max-height: 100%; }

.column {
  display: flex;
  flex-direction: column;
  flex: 1; }
  .column:not(:last-child) {
    margin-right: 32px; }
  .column section {
    padding: 32px;
    border-radius: 8px;
    background: #23313a;
    overflow: hidden;
    position: relative;
    display: flex;
    flex-direction: column; }
    .column section header {
      padding: 0 8px;
      margin-bottom: 16px; }
      .column section header.filter {
        padding: 0 8px;
        align-items: center; }
        .column section header.filter input {
          background: #2d3b44; }
          .column section header.filter input.regex {
            letter-spacing: 3px; }
    .column section:not(:last-child) {
      margin-bottom: 8px; }

/* --------------- */
/*  TABLES         */
/* --------------- */
table {
  table-layout: auto;
  border-collapse: collapse;
  border-spacing: 0;
  width: 100%; }

thead th, thead th span {
  text-transform: uppercase;
  font: normal 600 0.86em "Roboto condensed";
  color: #4c5e69;
  height: 40px;
  vertical-align: middle; }
  thead th:first-child, thead th span:first-child {
    padding-left: 8px; }
  thead th:last-child, thead th span:last-child {
    padding-right: 8px; }

tbody tr td {
  height: 28px;
  vertical-align: middle; }
  tbody tr td:first-child {
    padding-left: 8px;
    border-top-left-radius: 4px;
    border-bottom-left-radius: 4px; }
  tbody tr td:last-child {
    padding-right: 8px;
    border-top-right-radius: 4px;
    border-bottom-right-radius: 4px; }

tbody tr:hover td, tbody tr.selected td {
  background: #1d2a32; }

/* --------------- */
/*  HEADINGS         */
/* --------------- */
h1 {
  font: normal 500 1.8em "Roboto condensed";
  color: #a3ffd3; }

h2 {
  font: italic 500 1.66em "Roboto condensed";
  color: white; }

h3 {
  font: normal 500 1em "Roboto condensed";
  color: #a3ffd3;
  text-transform: uppercase; }

h4 {
  color: white;
  text-transform: uppercase; }

/* --------------- */
/*  FORMS         */
/* --------------- */
input[type=text] {
  border: none;
  border-radius: 4px;
  height: 48px;
  background: none;
  padding: 0 12px;
  font: normal 300 1em "Roboto";
  color: white;
  transition-delay: 0ms;
  transition-duration: 150ms;
  transition-property: opacity, transform, background, color, box-shadow;
  transition-timing-function: cubic-bezier(0.4, 0, 0.2, 1);
  transition-property: box-shadow, letter-spacing;
  box-shadow: 0px 0px 0px #0e1c26; }
  input[type=text]::-moz-placeholder {
    line-height: 48px; }
  input[type=text]::placeholder {
    font: italic 300 1em "Roboto";
    color: #4c5e69;
    letter-spacing: initial; }
  input[type=text]:focus {
    box-shadow: 8px 8px 0px rgba(14, 28, 38, 0.5); }

/* --------------- */
/*  BUTTONS         */
/* --------------- */
button {
  background: none;
  border: none;
  padding: 0 16px;
  position: relative;
  cursor: pointer;
  border-radius: 4px; }
  button.toggle {
    transition-delay: 0ms;
    transition-duration: 150ms;
    transition-property: opacity, transform, background, color, box-shadow;
    transition-timing-function: cubic-bezier(0.4, 0, 0.2, 1);
    font: italic 500 1.26em "Roboto condensed";
    color: #a3ffd3;
    box-shadow: inset 0 0 0 2px #a3ffd3; }
    button.toggle.active {
      box-shadow: inset 0 0 0 100px #a3ffd3;
      color: #23313a; }
  button.danger {
    color: #1d2a32;
    font: italic 500 1.26em "Roboto condensed";
    box-shadow: inset 0 0 0 2px #ff756f;
    background: #ff756f; }
  button.regular {
    color: #a3ffd3;
    font: italic 500 1.26em "Roboto condensed";
    box-shadow: inset 0 0 0 2px #2d3b44;
    background: #2d3b44; }

.top-header {
  display: flex;
  flex-direction: row;
  align-items: center;
  align-items: center;
  min-height: 112px;
  max-height: 112px;
  background: #1d2a32;
  padding-right: 48px;
  box-shadow: 0 30px 60px rgba(14, 28, 38, 0.3); }
  .top-header .logo {
    display: flex;
    flex-direction: row;
    justify-content: center;
    align-items: center;
    min-width: 120px;
    min-height: 112px;
    max-height: 112px;
    background: linear-gradient(135deg, #a3ffd3, #00ffd8); }
    .top-header .logo svg {
      fill: #1d2a32; }
  .top-header .breadcrumb {
    display: flex;
    flex-direction: row;
    align-items: center;
    padding-left: 64px; }
    .top-header .breadcrumb .item {
      position: relative;
      color: #4c5e69;
      font: normal 300 1.26em "Roboto condensed";
      padding-right: 24px;
      margin-right: 24px; }
      .top-header .breadcrumb .item:after {
        content: "\002F";
        position: absolute;
        right: 0; }

.context-actions {
  position: fixed;
  z-index: 99;
  top: 32px;
  right: 352px; }
  .context-actions .address {
    max-width: 310px;
    height: 48px;
    margin-left: 16px;
    border-radius: 4px;
    background: #2d3b44;
    overflow: hidden;
    position: relative; }
    .context-actions .address.zookeeper .key {
      color: #6fff98; }
    .context-actions .address.kafka .key {
      color: #f4f07c; }
    .context-actions .address .tooltip {
      font: normal 300 1em "Roboto";
      padding: 8px 12px;
      position: absolute;
      left: 50%;
      transform: translate(-50%, -16px);
      top: calc(100% + 4px);
      background: rgba(35, 49, 58, 0.8);
      border: 1px solid #23313a;
      border-radius: 4px;
      transition-delay: 0ms;
      transition-duration: 150ms;
      transition-property: opacity, transform, background, color, box-shadow;
      transition-timing-function: cubic-bezier(0.4, 0, 0.2, 1);
      opacity: 0;
      pointer-events: none; }
    .context-actions .address:hover {
      overflow: initial; }
      .context-actions .address:hover .tooltip {
        display: block;
        opacity: 1;
        pointer-events: auto;
        transform: none;
        transform: translate(-50%, 0); }
    .context-actions .address .key {
      font: italic 500 1em "Roboto condensed";
      text-transform: uppercase;
      padding-left: 16px;
      margin-right: 16px; }
    .context-actions .address .value {
      font: normal 300 1em "Roboto";
      overflow: hidden;
      padding-left: 16px;
      text-overflow: ellipsis; }
  .context-actions button {
    height: 48px;
    margin-left: 16px; }

.dropdown-menu .dropdown-menu-trigger {
  color: #a3c0cc;
  background: #2d3b44;
  min-width: 240px; }
  .dropdown-menu .dropdown-menu-trigger .mdi {
    color: #a3c0cc; }
  .dropdown-menu .dropdown-menu-trigger:hover {
    background: rgba(45, 59, 68, 0.8); }
  .dropdown-menu .dropdown-menu-trigger:active, .dropdown-menu .dropdown-menu-trigger:focus {
    background: rgba(45, 59, 68, 0.5); }

.dropdown-menu .dropdown-menu-content {
  background: #2d3b44; }
  .dropdown-menu .dropdown-menu-content .dropdown-menu-item {
    transition-delay: 0ms;
    transition-duration: 150ms;
    transition-property: opacity, transform, background, color, box-shadow;
    transition-timing-function: cubic-bezier(0.4, 0, 0.2, 1);
    color: #a3c0cc; }
    .dropdown-menu .dropdown-menu-content .dropdown-menu-item:hover, .dropdown-menu .dropdown-menu-content .dropdown-menu-item.selected {
      color: #a3ffd3; }
    .dropdown-menu .dropdown-menu-content .dropdown-menu-item:active {
      color: #a3ffd3; }

.sidebar {
  width: 120px;
  background: #1d2a32;
  padding-top: 0;
  display: flex;
  flex-direction: column;
  justify-content: space-between;
  position: relative;
  box-shadow: 30px 0 60px rgba(14, 28, 38, 0.3); }
  .sidebar .sidenav-items {
    display: flex;
    flex-direction: column;
    align-items: center; }
    .sidebar .sidenav-items .sidenav-item {
      position: relative;
      cursor: pointer;
      width: 120px;
      height: 120px;
      color: #809ca8;
      display: flex;
      flex-direction: row;
      justify-content: center;
      align-items: center;
      display: flex;
      flex-direction: column;
      align-items: center; }
      .sidebar .sidenav-items .sidenav-item .label {
        display: block;
        margin-top: 8px;
        color: #809ca8; }
      .sidebar .sidenav-items .sidenav-item svg {
        fill: #809ca8; }
      .sidebar .sidenav-items .sidenav-item.selected svg {
        fill: #a3ffd3; }
      .sidebar .sidenav-items .sidenav-item.selected .label {
        color: #a3ffd3; }
  .sidebar .theme-menu {
    padding: 8px;
    position: absolute;
    bottom: 0;
    left: 0;
    right: 0; }
    .sidebar .theme-menu .dropdown-menu-trigger {
      min-width: 100% !important;
      font-size: 16px !important;
      padding-right: 0 !important;
      border-radius: 0; }
    .sidebar .theme-menu .dropdown-menu-content {
