.danger, .topics-view .deleteTopicsModal h2 {
  color: #ff7d6c; }

.document-wrapper {
  display: flex;
  flex-direction: column;
  height: 100%; }

.page-wrapper {
  display: flex;
  flex: 1;
  max-height: calc(100vh - 112px); }

.content-wrapper {
  display: flex;
  flex-direction: column;
  flex: 1;
  padding: 8px; }

.grid-wrapper {
  display: flex;
  flex-direction: column;
  flex: 1;
  max-height: 100%; }

.grid {
  display: flex;
