/* --------------- */
/*  TABLES         */
/* --------------- */
table {
  table-layout: auto;
  border-collapse: collapse;
  border-spacing: 0;
  width: 100%; }

thead th, thead th span {
  text-transform: uppercase;
  font: normal 600 0.86em "Roboto condensed";
  color: #8787ab;
  height: 40px;
  vertical-align: middle; }
  thead th:first-child, thead th span:first-child {
    padding-left: 8px; }
  thead th:last-child, thead th span:last-child {
    padding-right: 8px; }

tbody tr td {
  height: 28px;
  vertical-align: middle; }
  tbody tr td:first-child {
    padding-left: 8px;
    border-top-left-radius: 4px;
    border-bottom-left-radius: 4px; }
  tbody tr td:last-child {
    padding-right: 8px;
    border-top-right-radius: 4px;
    border-bottom-right-radius: 4px; }

tbody tr:hover td, tbody tr.selected td {
  background: #454552; }

/* --------------- */
/*  HEADINGS         */
/* --------------- */
h1 {
  font: normal 500 1.8em "Roboto condensed";
  color: #f4fc7c; }

h2 {
  font: italic 500 1.66em "Roboto condensed";
  color: white; }

h3 {
  font: normal 500 1em "Roboto condensed";
  color: #f4fc7c;
  text-transform: uppercase; }

h4 {
  font: normal 500 1em "Roboto";
  color: white;
  text-transform: uppercase; }

/* --------------- */
/*  FORMS         */
/* --------------- */
input[type=text] {
  border: none;
  border-radius: 4px;
  height: 48px;
  background: none;
  padding: 0 12px;
  font: normal 300 1em "Roboto";
  color: white;
  transition-delay: 0ms;
  transition-duration: 150ms;
  transition-property: opacity, transform, background, color, box-shadow;
  transition-timing-function: cubic-bezier(0.4, 0, 0.2, 1);
  transition-property: box-shadow, letter-spacing;
  box-shadow: 0px 0px 0px #383842; }
  input[type=text]::-moz-placeholder {
    line-height: 48px; }
  input[type=text]::placeholder {
    font: italic 300 1em "Roboto";
    color: #8787ab;
    letter-spacing: initial; }
  input[type=text]:focus {
    box-shadow: 8px 8px 0px rgba(56, 56, 66, 0.5); }

/* --------------- */
/*  BUTTONS         */
/* --------------- */
button {
  background: none;
  border: none;
  padding: 0 16px;
  position: relative;
  cursor: pointer;
  border-radius: 4px; }
  button.toggle {
    transition-delay: 0ms;
    transition-duration: 150ms;
    transition-property: opacity, transform, background, color, box-shadow;
    transition-timing-function: cubic-bezier(0.4, 0, 0.2, 1);
    font: italic 500 1.26em "Roboto condensed";
    color: #f4fc7c;
    box-shadow: inset 0 0 0 2px #f4fc7c; }
    button.toggle.active {
      box-shadow: inset 0 0 0 100px #f4fc7c;
      color: #4a4a58; }
  button.danger {
    color: #454552;
    font: italic 500 1.26em "Roboto condensed";
    box-shadow: inset 0 0 0 2px #ff7d6c;
    background: #ff7d6c; }
  button.regular {
    color: #f4fc7c;
    font: italic 500 1.26em "Roboto condensed";
