/* --------------- */
/*  FORMS         */
/* --------------- */
input[type=text] {
  border: none;
  border-radius: 3px;
  height: 48px;
  background: none;
  padding: 0 12px;
  font: normal 300 1em "Roboto";
  color: #332e2e;
  transition-delay: 0ms;
  transition-duration: 150ms;
  transition-property: opacity, transform, background, color, box-shadow;
  transition-timing-function: cubic-bezier(0.4, 0, 0.2, 1);
  transition-property: box-shadow, letter-spacing;
  box-shadow: 0px 0px 0px #f9f7f7; }
  input[type=text]::-moz-placeholder {
    line-height: 48px; }
  input[type=text]::placeholder {
    font: italic 300 1em "Roboto";
    color: #9e9898;
    letter-spacing: initial; }
  input[type=text]:focus {
    box-shadow: 8px 8px 0px rgba(249, 247, 247, 0.5); }

/* --------------- */
/*  BUTTONS         */
/* --------------- */
button {
  background: none;
  border: none;
  padding: 0 16px;
  position: relative;
  cursor: pointer;
  border-radius: 3px; }
  button.toggle {
    transition-delay: 0ms;
    transition-duration: 150ms;
    transition-property: opacity, transform, background, color, box-shadow;
    transition-timing-function: cubic-bezier(0.4, 0, 0.2, 1);
    font: italic 500 1.26em "Patua One";
    color: #ef5350;
    box-shadow: inset 0 0 0 2px #ef5350; }
    button.toggle.active {
      box-shadow: inset 0 0 0 100px #ef5350;
      color: #efecec; }
  button.danger {
    color: #f2f0f0;
    font: italic 500 1.26em "Patua One";
    box-shadow: inset 0 0 0 2px #ef5350;
    background: #ef5350; }
  button.regular {
    color: #ef5350;
    font: italic 500 1.26em "Patua One";
    box-shadow: inset 0 0 0 2px #edeaea;
    background: #edeaea; }

