/* --------------- */
/*  LAYOUT         */
/* --------------- */
html {
  background: #4e4e4e; }

* {
  font: normal 300 1em "Roboto Mono";
  color: white; }

.danger, .topics-view .deleteTopicsModal h2 {
  color: #ff756f; }

.document-wrapper {
  display: flex;
  flex-direction: column;
  height: 100%; }

.page-wrapper {
  display: flex;
  flex: 1;
  max-height: calc(100vh - 112px); }

.content-wrapper {
  display: flex;
  flex-direction: column;
  flex: 1;
  padding: 0; }

.grid-wrapper {
  display: flex;
  flex-direction: column;
  flex: 1;
  max-height: 100%; }

.grid {
  display: flex;
  flex: 1;
  max-height: 100%; }

.column {
  display: flex;
  flex-direction: column;
  flex: 1; }
  .column:not(:last-child) {
    margin-right: 4px; }
  .column section {
    padding: 16px 16px 16px 4px;
    border-radius: 0px;
    background: #3a3a3a;
    overflow: hidden;
    position: relative;
    display: flex;
    flex-direction: column; }
    .column section header {
      padding: 0 8px;
      margin-bottom: 16px; }
      .column section header.filter {
        padding: 0 8px;
        align-items: center; }
        .column section header.filter input {
          background: #4e4e4e; }
          .column section header.filter input.regex {
            letter-spacing: 3px; }
    .column section:not(:last-child) {
      margin-bottom: 4px; }

/* --------------- */
/*  TABLES         */
/* --------------- */
table {
  table-layout: auto;
  border-collapse: collapse;
  border-spacing: 0;
  width: 100%; }

thead th, thead th span {
  text-transform: uppercase;
  font: normal 600 0.86em "Ubuntu Mono";
  color: #999999;
  height: 40px;
  vertical-align: middle; }
  thead th:first-child, thead th span:first-child {
    padding-left: 8px; }
  thead th:last-child, thead th span:last-child {
    padding-right: 8px; }

tbody tr td {
  height: 28px;
  vertical-align: middle; }
  tbody tr td:first-child {
    padding-left: 8px;
    border-top-left-radius: 4px;
    border-bottom-left-radius: 4px; }
  tbody tr td:last-child {
    padding-right: 8px;
    border-top-right-radius: 4px;
    border-bottom-right-radius: 4px; }

tbody tr:hover td, tbody tr.selected td {
  background: #424242; }

/* --------------- */
/*  HEADINGS         */
/* --------------- */
h1 {
  font: normal 500 1.8em "Ubuntu Mono";
  color: #34f999; }

h2 {
  font: italic 500 1.66em "Ubuntu Mono";
  color: white; }

h3 {
  font: normal 500 1.13em "Ubuntu Mono";
  color: #34f999;
  text-transform: uppercase; }

h4 {
  font: normal 500 1em "Roboto Mono";
  color: white;
  text-transform: uppercase; }

/* --------------- */
/*  FORMS         */
/* --------------- */
input[type=text] {
  border: none;
  border-radius: 0px;
  height: 48px;
  background: none;
  padding: 0 12px;
  font: normal 300 1em "Roboto Mono";
  color: white;
  transition-delay: 0ms;
  transition-duration: 150ms;
  transition-property: opacity, transform, background, color, box-shadow;
  transition-timing-function: cubic-bezier(0.4, 0, 0.2, 1);
  transition-property: box-shadow, letter-spacing;
  box-shadow: 0px 0px 0px #2a2a2a; }
  input[type=text]::-moz-placeholder {
    line-height: 48px; }
  input[type=text]::placeholder {
    font: italic 300 "Roboto Mono";
    color: #7e7e7e;
    letter-spacing: initial; }
  input[type=text]:focus {
    box-shadow: 8px 8px 0px rgba(42, 42, 42, 0.5); }

/* --------------- */
/*  BUTTONS         */
/* --------------- */
button {
  background: none;
  border: none;
  padding: 0 16px;
  position: relative;
  cursor: pointer;
  border-radius: 0px; }
  button.toggle {
    transition-delay: 0ms;
    transition-duration: 150ms;
    transition-property: opacity, transform, background, color, box-shadow;
    transition-timing-function: cubic-bezier(0.4, 0, 0.2, 1);
    font: italic 500 1.26em "Ubuntu Mono";
    color: #34f999;
    box-shadow: inset 0 0 0 2px #34f999; }
    button.toggle.active {
      box-shadow: inset 0 0 0 100px #34f999;
      color: #3a3a3a; }
  button.danger {
    color: #424242;
    font: italic 500 1.26em "Ubuntu Mono";
    box-shadow: inset 0 0 0 2px #ff756f;
    background: #ff756f; }
  button.regular {
    color: #34f999;
    font: italic 500 1.26em "Ubuntu Mono";
    box-shadow: inset 0 0 0 2px #4e4e4e;
    background: #4e4e4e; }

.top-header {
  display: flex;
  flex-direction: row;
  align-items: center;
  align-items: center;
  min-height: 112px;
  max-height: 112px;
  background: #424242;
  padding-right: 48px; }
  .top-header .logo {
    display: flex;
    flex-direction: row;
    justify-content: center;
    align-items: center;
    min-width: 120px;
    min-height: 112px;
    max-height: 112px;
    background: linear-gradient(135deg, #34f999, #34f999); }
    .top-header .logo svg {
      fill: #424242; }
  .top-header .breadcrumb {
    display: flex;
    flex-direction: row;
    align-items: center;
    padding-left: 64px; }
    .top-header .breadcrumb .item {
      position: relative;
      color: #7e7e7e;
      font: normal 300 1.26em "Ubuntu Mono";
      padding-right: 24px;
      margin-right: 24px; }
      .top-header .breadcrumb .item:after {
        content: "\002F";
        position: absolute;
        right: 0; }

.context-actions {
  position: fixed;
  z-index: 99;
  top: 32px;
  right: 352px; }
  .context-actions .address {
    max-width: 310px;
    height: 48px;
    margin-left: 16px;
    border-radius: 0px;
    background: #4e4e4e;
    overflow: hidden;
    position: relative; }
    .context-actions .address.zookeeper .key {
      color: #6fff98; }
    .context-actions .address.kafka .key {
      color: #f4f07c; }
    .context-actions .address .tooltip {
      font: normal 300 1em "Ubuntu Mono";
      padding: 8px 12px;
      position: absolute;
      left: 50%;
      transform: translate(-50%, -16px);
      top: calc(100% + 4px);
      background: rgba(58, 58, 58, 0.8);
      border: 1px solid #3a3a3a;
      border-radius: 0px;
      transition-delay: 0ms;
      transition-duration: 150ms;
      transition-property: opacity, transform, background, color, box-shadow;
      transition-timing-function: cubic-bezier(0.4, 0, 0.2, 1);
      opacity: 0;
      pointer-events: none; }
    .context-actions .address:hover {
      overflow: initial; }
      .context-actions .address:hover .tooltip {
        display: block;
        opacity: 1;
        pointer-events: auto;
        transform: none;
        transform: translate(-50%, 0); }
    .context-actions .address .key {
      font: italic 500 1em "Ubuntu Mono";
      text-transform: uppercase;
      padding-left: 16px;
      margin-right: 16px; }
    .context-actions .address .value {
      font: normal 300 1em "Ubuntu Mono";
      overflow: hidden;
      padding-left: 16px;
      text-overflow: ellipsis; }
  .context-actions button {
    height: 48px;
    margin-left: 16px; }

